using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI;  public class GlobalTimer : MonoBehaviour {     public bool isTakingTime = false;     public int currentSeconds = 150;      public GameObject timeDisplay01;     public GameObject timeDisplay02;  	// Use this for initialization 	void Start () { 		 	} 	 	// Update is called once per frame 	void Update () { 		if(!isTakingTime)         {             StartCoroutine(SubstractSecond());         } 	}      IEnumerator SubstractSecond()     {         isTakingTime = true;          currentSeconds -= 1;          timeDisplay01.GetComponent<Text>().text = "" + currentSeconds;         timeDisplay02.GetComponent<Text>().text = "" + currentSeconds;          yield return new WaitForSeconds(1);          isTakingTime = false;     } } 